// Validate RBI announcement data
const data = items[0].json;

// Required fields validation
const requiredFields = ['event_type', 'announcement', 'timestamp'];
for (const field of requiredFields) {
  if (!data[field]) {
    throw new Error(`Missing required field: ${field}`);
  }
}

// Event type validation
const validEvents = ['policy_decision', 'minutes_release', 'monetary_policy', 'rate_decision'];
if (!validEvents.includes(data.event_type)) {
  throw new Error(`Invalid event type: ${data.event_type}`);
}

// Add processing metadata
data.processed_at = new Date().toISOString();
data.source = 'RBI';
data.tier = 'TIER_1';
data.category = 'MONETARY_POLICY';

return [{ json: data }];

const data = items[0].json;
const announcement = data.announcement.toLowerCase();

// Extract key policy decisions
let policyDirection = 'NEUTRAL';
let rateChange = 0;
let impactMagnitude = 'MEDIUM';

// Rate change detection
if (announcement.includes('repo rate') || announcement.includes('policy rate')) {
  if (announcement.includes('increased') || announcement.includes('hiked') || announcement.includes('raised')) {
    policyDirection = 'HAWKISH';
    impactMagnitude = 'HIGH';
    // Try to extract basis points
    const bpsMatch = announcement.match(/(\d+)\s*basis\s*points?|bps/i);
    if (bpsMatch) rateChange = parseInt(bpsMatch[1]);
  } else if (announcement.includes('reduced') || announcement.includes('cut') || announcement.includes('lowered')) {
    policyDirection = 'DOVISH';
    impactMagnitude = 'HIGH';
    const bpsMatch = announcement.match(/(\d+)\s*basis\s*points?|bps/i);
    if (bpsMatch) rateChange = -parseInt(bpsMatch[1]);
  }
}

// Stance detection
if (announcement.includes('accommodative')) {
  policyDirection = 'DOVISH';
} else if (announcement.includes('neutral')) {
  policyDirection = 'NEUTRAL';
} else if (announcement.includes('withdraw') && announcement.includes('accommodation')) {
  policyDirection = 'HAWKISH';
}

// Enhanced event object
const enhancedEvent = {
  ...data,
  policy_direction: policyDirection,
  rate_change_bps: rateChange,
  impact_magnitude: impactMagnitude,
  market_sensitivity: 9.0,
  symbols_affected: ['NIFTY', 'BANKNIFTY', 'FINNIFTY'],
  execution_priority: 'IMMEDIATE'
};

return [{ json: enhancedEvent }];

const data = items[0].json;
const eventType = data.event_type;

let eventClassification = {
  category: 'MARKET_STRUCTURE',
  tier: 'TIER_2',
  urgency: 'MEDIUM',
  trading_action: 'MONITOR'
};

switch(eventType) {
  case 'circuit_filter':
    eventClassification.tier = 'TIER_1';
    eventClassification.urgency = 'HIGH';
    eventClassification.trading_action = 'PAUSE_NEW_TRADES';
    eventClassification.impact_duration = '15_MINUTES';
    break;

  case 'market_halt':
    eventClassification.tier = 'TIER_1';
    eventClassification.urgency = 'CRITICAL';
    eventClassification.trading_action = 'EMERGENCY_EXIT';
    eventClassification.impact_duration = 'UNKNOWN';
    break;

  case 'corporate_actions':
    eventClassification.tier = 'TIER_3';
    eventClassification.urgency = 'LOW';
    eventClassification.trading_action = 'ADJUST_POSITIONS';
    eventClassification.impact_duration = '1_DAY';
    break;
}

// Determine affected symbols
let affectedSymbols = ['NIFTY', 'BANKNIFTY'];
if (data.symbol) {
  affectedSymbols.push(data.symbol);
}
if (data.sector) {
  // Add sector-specific indices
  if (data.sector === 'BANKING') affectedSymbols.push('BANKNIFTY');
  if (data.sector === 'FINANCIAL') affectedSymbols.push('FINNIFTY');
}

const processedEvent = {
  ...data,
  ...eventClassification,
  symbols_affected: affectedSymbols,
  processed_at: new Date().toISOString(),
  source: 'NSE'
};

return [{ json: processedEvent }];

const data = items[0].json;
const now = new Date();
const hour = now.getHours();
const minute = now.getMinutes();

// IST trading hours: 9:15 AM to 3:30 PM
const isMarketOpen = (hour === 9 && minute >= 15) || 
                   (hour > 9 && hour < 15) || 
                   (hour === 15 && minute <= 30);

data.market_open = isMarketOpen;
data.processing_priority = isMarketOpen ? 'IMMEDIATE' : 'DELAYED';

// Adjust urgency based on market hours
if (!isMarketOpen && data.urgency !== 'CRITICAL') {
  data.urgency = 'LOW';
  data.trading_action = 'QUEUE_FOR_OPEN';
}

return [{ json: data }];

const data = items[0].json;
const validCompanies = ['RELIANCE', 'TCS', 'INFY', 'HDFCBANK', 'ICICIBANK', 'HDFC', 'ITC', 'KOTAKBANK', 'LT', 'AXISBANK'];

if (!data.company || !validCompanies.includes(data.company)) {
  throw new Error(`Invalid or unsupported company: ${data.company}`);
}

// Determine company tier and market impact
const tier1Companies = ['RELIANCE', 'TCS', 'HDFCBANK', 'ICICIBANK'];
const tier2Companies = ['INFY', 'HDFC', 'ITC', 'KOTAKBANK'];

let tier = 'TIER_3';
let marketImpact = 'MEDIUM';
let indexWeight = 'LOW';

if (tier1Companies.includes(data.company)) {
  tier = 'TIER_1';
  marketImpact = 'HIGH';
  indexWeight = 'HIGH';
} else if (tier2Companies.includes(data.company)) {
  tier = 'TIER_2';
  marketImpact = 'MEDIUM';
  indexWeight = 'MEDIUM';
}

// Determine affected indices
let affectedIndices = ['NIFTY'];
if (['HDFCBANK', 'ICICIBANK', 'AXISBANK', 'KOTAKBANK'].includes(data.company)) {
  affectedIndices.push('BANKNIFTY');
  affectedIndices.push('FINNIFTY');
}

data.tier = tier;
data.market_impact = marketImpact;
data.index_weight = indexWeight;
data.affected_indices = affectedIndices;
data.category = 'EARNINGS';
data.processed_at = new Date().toISOString();

return [{ json: data }];

const data = items[0].json;

// Extract key earnings metrics
let earningsAnalysis = {
  beat_miss_inline: 'UNKNOWN',
  surprise_factor: 'UNKNOWN',
  guidance_tone: 'NEUTRAL',
  execution_timing: 'NORMAL'
};

// Analyze earnings vs estimates
if (data.actual_eps && data.estimated_eps) {
  const actualEPS = parseFloat(data.actual_eps);
  const estimatedEPS = parseFloat(data.estimated_eps);
  const surprisePercent = ((actualEPS - estimatedEPS) / Math.abs(estimatedEPS)) * 100;

  if (surprisePercent > 10) {
    earningsAnalysis.beat_miss_inline = 'STRONG_BEAT';
    earningsAnalysis.surprise_factor = 'STRONG';
    earningsAnalysis.execution_timing = 'IMMEDIATE';
  } else if (surprisePercent > 5) {
    earningsAnalysis.beat_miss_inline = 'BEAT';
    earningsAnalysis.surprise_factor = 'MODERATE';
    earningsAnalysis.execution_timing = 'IMMEDIATE';
  } else if (surprisePercent >= -5) {
    earningsAnalysis.beat_miss_inline = 'INLINE';
    earningsAnalysis.surprise_factor = 'LOW';
    earningsAnalysis.execution_timing = 'DELAYED';
  } else if (surprisePercent >= -10) {
    earningsAnalysis.beat_miss_inline = 'MISS';
    earningsAnalysis.surprise_factor = 'MODERATE';
    earningsAnalysis.execution_timing = 'IMMEDIATE';
  } else {
    earningsAnalysis.beat_miss_inline = 'STRONG_MISS';
    earningsAnalysis.surprise_factor = 'STRONG';
    earningsAnalysis.execution_timing = 'IMMEDIATE';
  }

  data.surprise_percent = surprisePercent;
}

// Analyze guidance/commentary
if (data.management_commentary) {
  const commentary = data.management_commentary.toLowerCase();
  if (commentary.includes('positive') || commentary.includes('optimistic') || commentary.includes('strong outlook')) {
    earningsAnalysis.guidance_tone = 'POSITIVE';
  } else if (commentary.includes('challenging') || commentary.includes('headwinds') || commentary.includes('cautious')) {
    earningsAnalysis.guidance_tone = 'NEGATIVE';
  }
}

// Create enhanced event
const enhancedEvent = {
  ...data,
  ...earningsAnalysis,
  event_id: `EARNINGS_${data.company}_${new Date().getTime()}`,
  market_sensitivity: data.tier === 'TIER_1' ? 8.5 : data.tier === 'TIER_2' ? 7.0 : 5.5
};

return [{ json: enhancedEvent }];

const data = items[0].json;

let tradingAction = {
  recommended_action: 'MONITOR',
  position_sizing: 'NORMAL',
  time_horizon: 'INTRADAY',
  risk_level: 'MEDIUM'
};

// Determine action based on surprise and tier
if (data.surprise_factor === 'STRONG' && data.tier === 'TIER_1') {
  tradingAction.recommended_action = 'AGGRESSIVE_ENTRY';
  tradingAction.position_sizing = 'LARGE';
  tradingAction.risk_level = 'HIGH';
} else if (data.surprise_factor === 'MODERATE' && ['TIER_1', 'TIER_2'].includes(data.tier)) {
  tradingAction.recommended_action = 'ENTRY';
  tradingAction.position_sizing = 'NORMAL';
  tradingAction.risk_level = 'MEDIUM';
} else if (data.beat_miss_inline === 'INLINE') {
  tradingAction.recommended_action = 'MONITOR';
  tradingAction.position_sizing = 'SMALL';
  tradingAction.risk_level = 'LOW';
}

// Adjust for guidance
if (data.guidance_tone === 'POSITIVE' && tradingAction.recommended_action !== 'MONITOR') {
  tradingAction.position_sizing = tradingAction.position_sizing === 'LARGE' ? 'LARGE' : 'NORMAL';
} else if (data.guidance_tone === 'NEGATIVE') {
  tradingAction.risk_level = 'HIGH';
  if (tradingAction.recommended_action === 'AGGRESSIVE_ENTRY') {
    tradingAction.recommended_action = 'ENTRY';
  }
}

// Set execution window
if (data.execution_timing === 'IMMEDIATE') {
  tradingAction.execution_window = '5_MINUTES';
} else {
  tradingAction.execution_window = '15_MINUTES';
}

const finalEvent = {
  ...data,
  ...tradingAction,
  symbols_affected: data.affected_indices,
  created_at: new Date().toISOString()
};

return [{ json: finalEvent }];

