# Add this endpoint to your rest_api.py

@app.route('/api/events/n8n', methods=['POST'])
@jwt_required()
@async_route
async def receive_n8n_event():
    """Receive events from N8N workflows"""
    try:
        event_data = request.get_json()

        # Validate event data
        required_fields = ['event_id', 'category', 'tier']
        for field in required_fields:
            if field not in event_data:
                abort(400, description=f'Missing required field: {field}')

        # Get news impact strategy
        if orchestrator and 'enhanced_news_impact_scalper' in orchestrator.strategies:
            news_strategy = orchestrator.strategies['enhanced_news_impact_scalper']

            # Process event
            signals = await news_strategy.process_n8n_event(event_data)

            # Execute signals
            for signal in signals:
                await order_manager.execute_signal(signal)

            return jsonify({
                'success': True,
                'event_id': event_data['event_id'],
                'signals_generated': len(signals),
                'message': 'N8N event processed successfully'
            })
        else:
            abort(503, description='News impact strategy not available')

    except Exception as e:
        logger.error(f'Error processing N8N event: {e}')
        abort(500)
